sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var startDateForm = g_form.getValue('date_de_debut');
    var currentDate = new Date();

    // Extract year, month, and day components
    var year = currentDate.getFullYear();
    var month = String(currentDate.getMonth() + 1).padStart(2, '0'); // Add 1 because months are zero-indexed
    var day = String(currentDate.getDate()).padStart(2, '0');

    // Create a Date object for the current date
    var formattedDateCurrentDate = new Date(year, month - 1, day); // Subtract 1 from month to account for zero-indexing

    // Convert startDateForm to a Date object
    var formattedStartDate = new Date(startDateForm);

    // Compare the dates
    if (formattedStartDate < formattedDateCurrentDate) {
        g_form.addErrorMessage("You can't do that, start time is less than the current date");
    }
    g_form.addInfoMessage(formattedDateCurrentDate.toISOString().split('T')[0]); // Display current date in "YYYY-MM-DD" format
}
