sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var startDateForm = g_form.getValue('date_de_debut');
    var endDateForm = g_form.getValue('date_de_fin');

    // Convert start date and end date to Date objects
    var startDate = new Date(startDateForm);
    var endDate = new Date(endDateForm);

    // Check if end date is before start date
    if (endDate < startDate) {
        g_form.addErrorMessage("End date cannot be before the start date");
//         g_form.setValue('date_de_fin', ''); // Clear the end date field
    }
}
